function Details({route}) {
    const [advert, setAdvert] = useState({});
    const { id } = route.params;

    const fetchAdverts = async() => {
        try {
            const res = await axios.get(`http://10.0.2.2:3000/advert/${id}`, {
            })
            setAdvert(res.data);
        }
        catch(e) {
            console.error(e);
        }
    }

    useEffect(() => {
        fetchAdverts().catch();
    }, [])

    const openUrl = () => {
        Linking.openURL(advert.link);
    }

    return (
        <ScrollView style={styles.container}>
            <Center>
                <Image
                    style={styles.image}
                    source={lenovo}
                />
            </Center>
            <VStack>
                <Box>
                    <Text style={styles.title}>
                        {advert.title}
                    </Text>
                    <Text style={styles.price}>
                        {advert.price} zł
                    </Text>
                    <HStack style={styles.profile}>
                        <NativeBaseImage rounded='3xl' mr='2' source={{
                            uri: "data:image/jpeg;base64,"
                        }}
                               alt="Alternate Text"
                               size={"xs"}
                            style={styles.profile}
                        />
                        <VStack>
                            <Text style={{color: 'white'}}>Mikorolo</Text>
                            <Text style={{color: 'white'}}>{moment(advert.date).format('lll')}</Text>
                        </VStack>
                    </HStack>
                    <Button style={styles.checkOccasion} onPress={() => openUrl()}>
                        Check occasion
                    </Button>
                </Box>
                <Text style={styles.heading}>
                    Description:
                </Text>
                    <ScrollView style={styles.descriptionBox}>
                        <Text style={styles.descriptionText}>
                            {advert.description}
                        </Text>
                    </ScrollView>
            </VStack>
            <Box style={styles.commentsSection}>
                <Text style={styles.heading}>
                    Comments:
                </Text>
                {comments.list.map((item) => {
                    return (
                        <HStack style={styles.comments} key={item.id}>
                            <NativeBaseImage rounded='3xl' mr='2' source={{
                                uri: item.image
                            }}
                                             alt="Alternate Text"
                                             size={"xs"}
                                             style={styles.profile}
                            />
                            <VStack>
                                <Text style={{color: 'white', fontWeight: 'bold'}}>{item.user}</Text>
                                <Text style={{color: 'white'}}>{item.comment}</Text>
                            </VStack>
                        </HStack>
                    )
                })}


            </Box>
        </ScrollView>
    );
}

export default Details;